--TABLES
SET default_tablespace = ts_licpub_data;
SET default_with_oids = false;

--Se crea tabla grupo
CREATE TABLE if not exists licpub.grupo
(
    id_grupo integer NOT NULL,
    id_division integer NOT NULL,
    descripcion character varying(500) NOT NULL
)TABLESPACE ts_licpub_data;

ALTER TABLE licpub.grupo OWNER TO licpub;

SET default_tablespace = ts_licpub_idx;

ALTER TABLE licpub.grupo ADD CONSTRAINT grupo_pkey PRIMARY KEY (id_grupo)
	USING INDEX TABLESPACE ts_licpub_idx;

ALTER TABLE ONLY licpub.grupo
    ADD CONSTRAINT grupo_fk FOREIGN KEY (id_division)
    REFERENCES licpub.division(id_division) DEFERRABLE INITIALLY DEFERRED;

