--TABLES
SET default_tablespace = ts_licpub_data;
SET default_with_oids = false;

--Se crea tabla cpv
CREATE TABLE if not exists licpub.cpv
(
    id_cpv integer NOT NULL,
    id_division integer NOT NULL,
    id_grupo integer NULL,
    id_clase integer NULL,
    id_categoria integer NULL,
    descripcion character varying(500) NOT NULL
)TABLESPACE ts_licpub_data;

ALTER TABLE licpub.cpv OWNER TO licpub;

SET default_tablespace = ts_licpub_idx;

ALTER TABLE licpub.cpv ADD CONSTRAINT cpv_pkey PRIMARY KEY (id_cpv)
	USING INDEX TABLESPACE ts_licpub_idx;

ALTER TABLE ONLY licpub.cpv
    ADD CONSTRAINT cpv_fk FOREIGN KEY (id_division)
    REFERENCES licpub.division(id_division) DEFERRABLE INITIALLY DEFERRED;

ALTER TABLE ONLY licpub.cpv
    ADD CONSTRAINT cpv_fk1 FOREIGN KEY (id_grupo)
    REFERENCES licpub.grupo(id_grupo) DEFERRABLE INITIALLY DEFERRED;

ALTER TABLE ONLY licpub.cpv
    ADD CONSTRAINT cpv_fk2 FOREIGN KEY (id_clase)
    REFERENCES licpub.clase(id_clase) DEFERRABLE INITIALLY DEFERRED;

ALTER TABLE ONLY licpub.cpv
    ADD CONSTRAINT cpv_fk3 FOREIGN KEY (id_categoria)
    REFERENCES licpub.categoria(id_categoria) DEFERRABLE INITIALLY DEFERRED;

